import React, { useState, useEffect } from 'react';
import { FaBars } from 'react-icons/fa';
import {
    Nav, NavbarContainer, NavLogo, MobileIcon,
    NavMenu, NavItem, NavLinks
} from "./NavbarElements"
import Logo from "../Logo"
import { animateScroll as scroll } from "react-scroll";

const Navbar = ({ toggle }) => {

    const [scrollNav, setScrollNav] = useState(false);

    const changeNav = () => {
        if (window.scrollY >= 300) {
            setScrollNav(true);
        } else {
            setScrollNav(false);
        }
    }

    useEffect(() => {
        window.addEventListener('scroll', changeNav)
    }, []);

    const toggleHome = () => {
        scroll.scrollToTop();
    }

    return (
        <>
            <Nav scrollNav={scrollNav}>
                <NavbarContainer>
                    <NavLogo to="/" onClick={toggleHome}>
                        <Logo />
                    </NavLogo>
                    <MobileIcon onClick={toggle}>
                        <FaBars />
                    </MobileIcon>
                    <NavMenu>
                        <NavItem>
                            <NavLinks to="about"
                                smooth="true"
                                duration={500}
                                spy={true}
                                exact="true"
                                offset={-80}
                                activeClass="active"
                            > About </NavLinks>
                        </NavItem>
                        <NavItem>
                            <NavLinks to="schedule"
                                smooth="true"
                                duration={500}
                                spy={true}
                                exact="true"
                                offset={-80}
                                activeClass="active"
                            >Schedule</NavLinks>
                        </NavItem>
                        <NavItem>
                            <NavLinks to="team"
                                smooth="true"
                                duration={500}
                                spy={true}
                                exact="true"
                                offset={-80}
                                activeClass="active"
                            >Domains</NavLinks>
                        </NavItem>
                        {/* <NavItem>
                            <NavLinks to="prizes"
                                smooth="true"
                                duration={500}
                                spy={true}
                                exact="true"
                                offset={-80}
                                activeClass="active"
                            >Prizes</NavLinks>
                        </NavItem> */}
                        <NavItem>
                            <NavLinks to="sponsors"
                                smooth="true"
                                duration={500}
                                spy={true}
                                exact="true"
                                offset={-80}
                                activeClass="active"
                            >Sponsors</NavLinks>
                        </NavItem>
                        {/* <NavItem>
                            <NavLinks to="judges"
                                smooth="true"
                                duration={500}
                                spy={true}
                                exact="true"
                                offset={-80}
                                activeClass="active"
                            >Judges</NavLinks>
                        </NavItem> */}
                        <NavItem>
                            <NavLinks to="FAQs"
                                smooth="true"
                                duration={500}
                                spy={true}
                                exact="true"
                                offset={-80}
                                activeClass="active"
                            >FAQs</NavLinks>
                        </NavItem>
                        {/* <NavItem>
                            <NavLinks to="team"
                                smooth="true"
                                duration={500}
                                spy={true}
                                exact="true"
                                offset={-80}
                                activeClass="active"
                            >Team</NavLinks>
                        </NavItem> */}
                    </NavMenu>
                </NavbarContainer>
            </Nav>
        </>
    )
}

export default Navbar
